// SPDX-License-Identifier: MIT
pragma solidity ^0.8.20;

/**
 * @title EscrowContract
 * @dev x402 programmable escrow for AutoFlow task payments
 */
contract EscrowContract {
    struct Escrow {
        uint256 id;
        address agent;
        address node;
        uint256 amount;
        uint256 lockedAt;
        uint256 timeoutAt;
        EscrowStatus status;
        bytes32 taskHash;
    }

    enum EscrowStatus { Locked, Released, Refunded, Disputed, Slashed }

    uint256 public escrowCounter;
    mapping(uint256 => Escrow) public escrows;
    mapping(bytes32 => uint256) public taskToEscrow;

    address public orchestrator; // Backend address
    uint256 public platformFee = 200; // 2% in basis points
    address public feeRecipient;

    event EscrowLocked(uint256 indexed escrowId, address indexed agent, uint256 amount, bytes32 taskHash);
    event EscrowReleased(uint256 indexed escrowId, address indexed node, uint256 amount);
    event EscrowRefunded(uint256 indexed escrowId, address indexed agent, uint256 amount);
    event EscrowSlashed(uint256 indexed escrowId, uint256 slashAmount, string reason);

    modifier onlyOrchestrator() {
        require(msg.sender == orchestrator, "Only orchestrator");
        _;
    }

    constructor(address _feeRecipient) {
        orchestrator = msg.sender;
        feeRecipient = _feeRecipient;
    }

    function lockEscrow(bytes32 taskHash, uint256 timeoutHours) external payable returns (uint256) {
        require(msg.value > 0, "Must send funds");
        require(taskToEscrow[taskHash] == 0, "Task already escrowed");

        escrowCounter++;
        uint256 escrowId = escrowCounter;

        escrows[escrowId] = Escrow({
            id: escrowId,
            agent: msg.sender,
            node: address(0),
            amount: msg.value,
            lockedAt: block.timestamp,
            timeoutAt: block.timestamp + (timeoutHours * 1 hours),
            status: EscrowStatus.Locked,
            taskHash: taskHash
        });

        taskToEscrow[taskHash] = escrowId;

        emit EscrowLocked(escrowId, msg.sender, msg.value, taskHash);
        return escrowId;
    }

    function releaseEscrow(uint256 escrowId, address nodeAddress) external onlyOrchestrator {
        Escrow storage escrow = escrows[escrowId];
        require(escrow.status == EscrowStatus.Locked, "Invalid status");
        require(block.timestamp < escrow.timeoutAt, "Escrow timed out");

        escrow.status = EscrowStatus.Released;
        escrow.node = nodeAddress;

        uint256 fee = (escrow.amount * platformFee) / 10000;
        uint256 nodePayment = escrow.amount - fee;

        payable(feeRecipient).transfer(fee);
        payable(nodeAddress).transfer(nodePayment);

        emit EscrowReleased(escrowId, nodeAddress, nodePayment);
    }

    function refundEscrow(uint256 escrowId) external {
        Escrow storage escrow = escrows[escrowId];
        require(escrow.status == EscrowStatus.Locked, "Invalid status");
        require(
            block.timestamp >= escrow.timeoutAt || msg.sender == orchestrator,
            "Cannot refund yet"
        );

        escrow.status = EscrowStatus.Refunded;

        payable(escrow.agent).transfer(escrow.amount);

        emit EscrowRefunded(escrowId, escrow.agent, escrow.amount);
    }

    function slashEscrow(
        uint256 escrowId,
        uint256 slashPercentage,
        string memory reason
    ) external onlyOrchestrator {
        Escrow storage escrow = escrows[escrowId];
        require(escrow.status == EscrowStatus.Locked, "Invalid status");
        require(slashPercentage <= 100, "Invalid percentage");

        escrow.status = EscrowStatus.Slashed;

        uint256 slashAmount = (escrow.amount * slashPercentage) / 100;
        uint256 refundAmount = escrow.amount - slashAmount;

        if (slashAmount > 0) {
            payable(feeRecipient).transfer(slashAmount);
        }

        if (refundAmount > 0) {
            payable(escrow.agent).transfer(refundAmount);
        }

        emit EscrowSlashed(escrowId, slashAmount, reason);
    }

    function getEscrow(uint256 escrowId) external view returns (Escrow memory) {
        return escrows[escrowId];
    }

    function updateOrchestrator(address newOrchestrator) external onlyOrchestrator {
        orchestrator = newOrchestrator;
    }
}

contracts/PaymentSettlement.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.20;

/**
 * @title PaymentSettlement
 * @dev Handles direct payments and batch settlements for AutoFlow
 */
contract PaymentSettlement {
    struct Payment {
        uint256 id;
        address from;
        address to;
        uint256 amount;
        uint256 timestamp;
        PaymentType paymentType;
        bytes32 taskRef;
    }

    enum PaymentType { Direct, Settlement, Refund, Bonus }

    uint256 public paymentCounter;
    mapping(uint256 => Payment) public payments;
    mapping(address => uint256[]) public userPayments;
    mapping(address => uint256) public balances;

    address public orchestrator;
    uint256 public platformFee = 100; // 1% in basis points
    address public feeRecipient;

    event PaymentMade(uint256 indexed paymentId, address indexed from, address indexed to, uint256 amount);
    event Deposited(address indexed user, uint256 amount);
    event Withdrawn(address indexed user, uint256 amount);
    event BatchSettled(uint256 totalAmount, uint256 paymentCount);

    modifier onlyOrchestrator() {
        require(msg.sender == orchestrator, "Only orchestrator");
        _;
    }

    constructor() {
        orchestrator = msg.sender;
        feeRecipient = msg.sender;
    }

    // Users can deposit funds to their balance
    function deposit() external payable {
        require(msg.value > 0, "Must send funds");
        balances[msg.sender] += msg.value;
        emit Deposited(msg.sender, msg.value);
    }

    // Users can withdraw their balance
    function withdraw(uint256 amount) external {
        require(balances[msg.sender] >= amount, "Insufficient balance");
        balances[msg.sender] -= amount;
        payable(msg.sender).transfer(amount);
        emit Withdrawn(msg.sender, amount);
    }

    // Direct payment from one user to another
    function pay(
        address to,
        uint256 amount,
        bytes32 taskRef
    ) external {
        require(balances[msg.sender] >= amount, "Insufficient balance");
        require(to != address(0), "Invalid recipient");

        uint256 fee = (amount * platformFee) / 10000;
        uint256 netAmount = amount - fee;

        balances[msg.sender] -= amount;
        balances[to] += netAmount;
        balances[feeRecipient] += fee;

        paymentCounter++;
        payments[paymentCounter] = Payment({
            id: paymentCounter,
            from: msg.sender,
            to: to,
            amount: netAmount,
            timestamp: block.timestamp,
            paymentType: PaymentType.Direct,
            taskRef: taskRef
        });

        userPayments[msg.sender].push(paymentCounter);
        userPayments[to].push(paymentCounter);

        emit PaymentMade(paymentCounter, msg.sender, to, netAmount);
    }

    // Orchestrator can settle payments in batch
    function batchSettle(
        address[] calldata recipients,
        uint256[] calldata amounts,
        bytes32[] calldata taskRefs
    ) external onlyOrchestrator {
        require(recipients.length == amounts.length, "Array mismatch");
        require(recipients.length == taskRefs.length, "Array mismatch");

        uint256 totalAmount = 0;

        for (uint256 i = 0; i < recipients.length; i++) {
            require(recipients[i] != address(0), "Invalid recipient");

            paymentCounter++;
            payments[paymentCounter] = Payment({
                id: paymentCounter,
                from: address(this),
                to: recipients[i],
                amount: amounts[i],
                timestamp: block.timestamp,
                paymentType: PaymentType.Settlement,
                taskRef: taskRefs[i]
            });

            balances[recipients[i]] += amounts[i];
            userPayments[recipients[i]].push(paymentCounter);
            totalAmount += amounts[i];

            emit PaymentMade(paymentCounter, address(this), recipients[i], amounts[i]);
        }

        emit BatchSettled(totalAmount, recipients.length);
    }

    function getBalance(address user) external view returns (uint256) {
        return balances[user];
    }

    function getPayment(uint256 paymentId) external view returns (Payment memory) {
        return payments[paymentId];
    }

    function getUserPaymentCount(address user) external view returns (uint256) {
        return userPayments[user].length;
    }

    function setFeeRecipient(address newFeeRecipient) external onlyOrchestrator {
        feeRecipient = newFeeRecipient;
    }

    function setPlatformFee(uint256 newFee) external onlyOrchestrator {
        require(newFee <= 1000, "Fee too high"); // Max 10%
        platformFee = newFee;
    }

    function updateOrchestrator(address newOrchestrator) external onlyOrchestrator {
        orchestrator = newOrchestrator;
    }

    // Allow contract to receive ETH
    receive() external payable {
        balances[msg.sender] += msg.value;
        emit Deposited(msg.sender, msg.value);
    }
}

